public class PrimitiveDataType {

	/**
	 * @param args
	 */
	
	static boolean a;
	static char b;
	static byte c;
	static short d;
	static int e;
	static long f;
	static float g;
	static double h;
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

	System.out.println("Below are the primitive data types default value");
	
		System.out.println("Default value of boolean is = " + a);
		System.out.println("Default value of char is = " + b);
		System.out.println("Default value of byte is = " + c);
		System.out.println("Default value of short is = " + d);
		System.out.println("Default value of int is = " + e);
		System.out.println("Default value of long is = " + f);
		System.out.println("Default value of float is = " + g);
		System.out.println("Default value of double is = " + h);
	}

}
